#! /usr/bin/env python#*-* coding=utf-8 -*-import osimport sysimport logging.configdef mylog(logFileName=None, stdoutOn=False, logdir=None):        logfile = logFileName if logFileName else "log.log"    gLogger = logging.getLogger()    if logdir == None:        logdir = ""        if logdir != "":        os.system("mkdir -p "+logdir)        logfile = "./%s/%s"%(logdir, logfile)        if os.path.exists(logfile):        os.remove(logfile)        if stdoutOn != False:        formatter = logging.Formatter('[%(asctime)s][%(levelname)s] %(message)s')        handler = logging.StreamHandler(sys.stdout)        handler.setFormatter(formatter)        gLogger.addHandler(handler)        formatter = logging.Formatter('[%(asctime)s][%(levelname)s][%(lineno)s] %(message)s')    handler = logging.handlers.RotatingFileHandler(logfile)    handler.setFormatter(formatter)    gLogger.addHandler(handler)    gLogger.setLevel(logging.DEBUG)    return gLoggerlogger = mylog(stdoutOn=True)